package com.example.randomphotogenerator

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Button
import android.widget.ImageView
import android.widget.Toast
import com.bumptech.glide.Glide

class MainActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)


        val shuffleButton = findViewById<Button>(R.id.shuffleButton)
        val imageView = findViewById<ImageView>(R.id.randomImageView)
        val url = listOf<String>(
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRok_yQeA5u03_tShfSrEh1P_SscOHQB_xMMwyWYEt0mir2HHW1VkMgNHmFX3YNpWlA7iM&usqp=CAU",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShZjTRa3YQBDzWsRbW3a2AqHxoa_b2io5HptbkGvmUuBpID4Rjhno6qRQPzHP2Evma7vs&usqp=CAU",
            "data:image/jpeg;base64,"
            )




        shuffleButton.setOnClickListener() {
            var order = (0..2).random()
            Glide.with(this).load(url[order]).fitCenter().into(imageView)
            Toast.makeText(this, "button clicked order:$order", Toast.LENGTH_LONG).show()
        }

    }
}